import React, { useState, useRef, useEffect } from "react";
import Hero from "./Components/Hero";
import HotelCard from "./Components/HotelCard";
import ContactForm from "./Components/ContactForm";
import Footer from "./Components/Footer";
import WhatsAppButton from "./Components/WhatsAppbutton";
import AccessibilityWidget from "./Components/AccessibilityWidget";

export default function Index() {
  const [selectedHotel, setSelectedHotel] = useState("");
  const contactRef = useRef(null);
  const hotelsRef = useRef(null);

  const hotels = [
    {
      id: "aqueduct",
      title: "מלון אקוודוקט | רגבה",
      image: "https://images.unsplash.com/photo-1566073771259-6a8506099945?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80",
      prices: [
        "מחיר לזוג: 1400 ש״ח",
        "ילדים (2–12): 350 ש״ח",
        "תינוקות (0–2): 80 ש״ח"
      ]
    },
    {
      id: "kinneret",
      title: "מלון על ים כנרת",
      image: "https://images.unsplash.com/photo-1544140708-15f9043f7cc9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80",
      prices: [
        "מחיר לזוג: 1380 ש״ח",
        "ילדים (2–12): 350 ש״ח",
        "תינוקות (0–2): 80 ש״ח"
      ]
    },
    {
      id: "eingedi",
      title: "מלון עין גדי – גן הפקאן",
      image: "https://images.unsplash.com/photo-1615460549969-36fa19521a4f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80",
      prices: [
        "1500 ש״ח לזוג",
        "בסיס חצי פנסיון"
      ]
    },
    {
      id: "eilat",
      title: "מלון רימונים אילת",
      image: "https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=800&q=80",
      prices: [
        "מחיר לזוג: 1600 ש״ח",
        "ילדים (2–12): 400 ש״ח",
        "תינוקות (0–2): 100 ש״ח"
      ]
    }
  ];

  const handleHotelSelect = (hotelId) => {
    setSelectedHotel(hotelId);
    contactRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleFormSubmit = async (formData) => {
    try {
      const response = await fetch('https://hook.eu2.make.com/3sncfmuct8l6mlw6zcej3f4yxpr2qdln', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      return true;
    } catch (error) {
      console.error('Error submitting form:', error);
      return false;
    }
  };

  // Add Hebrew fonts
  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      @import url('https://fonts.googleapis.com/css2?family=Assistant:wght@400;600;700&family=Rubik:wght@400;500;600;700&display=swap');

      :root {
        --font-heading: 'Assistant', sans-serif;
        --font-body: 'Rubik', sans-serif;
      }

      h1, h2, h3, h4, h5, h6 {
        font-family: var(--font-heading);
      }

      body {
        font-family: var(--font-body);
      }
    `;
    document.head.appendChild(style);
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div dir="rtl" className="min-h-screen flex flex-col">
      <Hero 
        scrollToContact={() => contactRef.current?.scrollIntoView({ behavior: 'smooth' })}
        scrollToHotels={() => hotelsRef.current?.scrollIntoView({ behavior: 'smooth' })}
      />

      <div className="flex flex-col md:flex-row justify-center items-center gap-4 py-8 bg-white">
        <button 
          onClick={() => hotelsRef.current?.scrollIntoView({ behavior: 'smooth' })}
          className="bg-yellow-400 hover:bg-yellow-500 text-blue-900 w-64 py-6 text-lg"
        >
          צפייה במלונות
        </button>
        <button 
          onClick={() => contactRef.current?.scrollIntoView({ behavior: 'smooth' })}
          className="bg-blue-500 hover:bg-blue-600 text-white w-64 py-6 text-lg"
        >
          צור קשר
        </button>
      </div>

      <section ref={hotelsRef} id="hotels" className="py-12 md:py-16 bg-blue-50">
        <div className="container mx-auto px-4">
          <h2 className="text-2xl md:text-3xl font-bold text-blue-900 mb-8 text-center">
            המלונות שלנו
          </h2>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {hotels.map((hotel) => (
              <HotelCard 
                key={hotel.id}
                hotel={hotel}
                onBookClick={handleHotelSelect}
              />
            ))}
          </div>
        </div>
      </section>

      <ContactForm 
        contactRef={contactRef}
        hotels={hotels}
        selectedHotel={selectedHotel}
        onHotelSelect={setSelectedHotel}
      />

      <Footer />
      <WhatsAppButton selectedHotel={selectedHotel} hotels={hotels} />
      <AccessibilityWidget />
    </div>
  );
}
